mapper.select:
        select
role_id, menu_id, created, create_id, updated, update_id, is_delete
        from sms_role_menu


                and role_id = #{roleId}


                and menu_id = #{menuId}


                and created = #{created}


                and create_id = #{createId}


                and updated = #{updated}


                and update_id = #{updateId}


                and is_delete = #{isDelete}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sms_role_menu


        and role_id = #{roleId}


        and menu_id = #{menuId}


        and created = #{created}


        and create_id = #{createId}


        and updated = #{updated}


        and update_id = #{updateId}


        and is_delete = #{isDelete}
