mapper.select:
        select
id, username, password, salt, nickname, created, create_id, updated, update_id, is_delete, extend1, extend2, extend3, extend4
        from sms_user


                and id = #{id}


                and username = #{username}


                and password = #{password}


                and salt = #{salt}


                and nickname = #{nickname}


                and created = #{created}


                and create_id = #{createId}


                and updated = #{updated}


                and update_id = #{updateId}


                and is_delete = #{isDelete}


                and extend1 = #{extend1}


                and extend2 = #{extend2}


                and extend3 = #{extend3}


                and extend4 = #{extend4}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sms_user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and salt = #{salt}


        and nickname = #{nickname}


        and created = #{created}


        and create_id = #{createId}


        and updated = #{updated}


        and update_id = #{updateId}


        and is_delete = #{isDelete}


        and extend1 = #{extend1}


        and extend2 = #{extend2}


        and extend3 = #{extend3}


        and extend4 = #{extend4}
