mapper.select:
        select
user_id, role_id, created, create_id, updated, update_id, is_delete
        from sms_user_role


                and user_id = #{userId}


                and role_id = #{roleId}


                and created = #{created}


                and create_id = #{createId}


                and updated = #{updated}


                and update_id = #{updateId}


                and is_delete = #{isDelete}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sms_user_role


        and user_id = #{userId}


        and role_id = #{roleId}


        and created = #{created}


        and create_id = #{createId}


        and updated = #{updated}


        and update_id = #{updateId}


        and is_delete = #{isDelete}
